import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, of} from 'rxjs';
import {map} from 'rxjs/operators';
import {SelectSnapshot} from "@ngxs-labs/select-snapshot";
import {StatusState} from "../../state/status/status.state";
import {SeriesModel} from "../models/data";

@Injectable({
  providedIn: 'root'
})
export class CacheSeriesService {

  private _cacheUrls: {
    idx: number,
    category: string,
    url: string
  }[] = [];
  private _cachedSeries: SeriesModel[] =[];
  @SelectSnapshot(StatusState.getCategoryList) category_list: string[] | undefined;
  constructor(private http: HttpClient) { }

  isThisUrlCached(url: string) {
    return this._cacheUrls.find(val => val.url === url);
  }
  getCachedSeriesByCat(cat: string) { // data: SeriesModel
    return  this._cachedSeries.filter(val => val.category === cat)[0];
  }

  getCacheUrls() {
    return this._cacheUrls;
  }

  // @ts-ignore
  get cachedSeries(): SeriesModel[] {
    return this._cachedSeries;
  }
  getTotalSeriesList(url: string) {
    return this.http.get(url).pipe(
      map ( (res:any) => res['data']),
    )
  }

  getCachedSeriesByUrl(url: string) {
    return this._cachedSeries.filter(series => series.url === url)[0];
  }

  checkAndCacheSeries(data: SeriesModel) {
    this._cachedSeries.push(data);
  }
  readFile (blob: any): Observable<string>  {
    return new Observable((obs: any) => {
      const reader = new FileReader();

      reader.onerror = err => obs.error(err);
      reader.onabort = err => obs.error(err);
      reader.onload = () => obs.next(reader.result);
      reader.onloadend = () => obs.complete();

      return reader.readAsDataURL(blob);
    });
  }
}

import { Injectable } from '@angular/core';
import {ImageService} from "./image.service";
import {Store} from "@ngxs/store";
// import {SetSelectedImageById} from "../../store/status/status.actions";
import {SelectSnapshot} from "@ngxs-labs/select-snapshot";
import {StatusState} from "../../state/status/status.state";
import {SplitService} from "./split.service";
import {SetSelectedImageById} from "../../state/status/status.actions";
import {ImageModel} from "../models/data";

@Injectable({
  providedIn: 'root'
})
export class CarouselService {
  // @Select(StatusState.getSelectedImageById) getSelectedImageById$: Observable<number>;
  @SelectSnapshot(StatusState.getCurrentCategory) category: string;
  constructor(
    private imageService: ImageService,
    private splitService: SplitService,
    private store: Store
    ) {}
  getNextImage(cat: string, element: any) {
    if( this.imageService.getCacheUrlsByCategory(cat).length > this.splitService.currentImageIndex[cat] + 1) {
      this.splitService.currentImageIndex[cat] = this.splitService.currentImageIndex[cat] + 1;
    }
    // if( this.imageService.getCacheUrlsByCategory(cat).length > this.splitService.currentImageIndex[element] + 1) {
    //   this.splitService.currentImageIndex[element] = this.splitService.currentImageIndex[element] + 1;
    // }
    const image: ImageModel = {
      imageId: this.splitService.currentImageIndex[cat],
      // imageId: this.splitService.currentImageIndex[element],
      category: cat,
      url: '',
      blob: '',
      title: ''
    }
    /**
     * new SetSelectedImageById --> getSelectedImageById(carousel-main.compoment)
     * new SetSelectedSplitWindow(carousel-main.component displayFirstImage)
     * --> onSelectTemplate (grid component)
     * */

    this.store.dispatch(new SetSelectedImageById(image));
    //
    return this.imageService.getCacheImage(cat, this.splitService.currentImageIndex[cat]);
    // return this.imageService.getCacheImage(cat, this.splitService.currentImageIndex[element]);
    //
  }
  getPrevImage(cat: string, element: any) {
    if( this.splitService.currentImageIndex[cat] > 0) {
      this.splitService.currentImageIndex[cat] = this.splitService.currentImageIndex[cat] - 1;
    }
    // if( this.splitService.currentImageIndex[element] > 0) {
    //   this.splitService.currentImageIndex[element] = this.splitService.currentImageIndex[element] - 1;
    // }
    // console.log('-- prev this.currentImageIndex', this.currentImageIndex )
    const image: ImageModel = {
      imageId: this.splitService.currentImageIndex[cat],
      // imageId: this.splitService.currentImageIndex[element],
      category: cat,
      url: '',
      blob: '',
      title: ''
    }

    this.store.dispatch(new SetSelectedImageById(image));
    return this.imageService.getCacheImage(this.category, this.splitService.currentImageIndex[cat]);
    // return this.imageService.getCacheImage(this.category, this.splitService.currentImageIndex[element]);
  }
  getSelectedImageByUrl(url: string, element: any) {
    // return this.imageService.getCacheImage(this.category, this.splitService.currentImageIndex[element])
    // return this.imageService.getCacheImage(this.category, this.splitService.currentImageIndex[element])
  }
  getSelectedImageById(cat: string, idx: number) {
    return this.imageService.getCacheImage(cat, idx)
  }
}

import {
  AfterViewInit,
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component, EventEmitter,
  Injectable,
  Input, OnDestroy,
  OnInit, Output,
  ViewChild
} from '@angular/core';
import {
  CdkVirtualScrollViewport,
  FixedSizeVirtualScrollStrategy,
  VIRTUAL_SCROLL_STRATEGY
} from "@angular/cdk/scrolling";
import {Select} from "@ngxs/store";
import {StatusState} from "../../../../state/status/status.state";

import {Observable, Subject} from "rxjs";
import {ImageModel} from "../../../models/data";
import {take, takeUntil} from "rxjs/operators";
@Injectable()
export class CustomVirtualScrollStrategy extends FixedSizeVirtualScrollStrategy {
  constructor() {
    /** Below value is assumed, that could contain at most 100 image pixel data at one time.
     * If less than this value, the image data tend to be shuffled by sharing memory usage while scrolling  */
    super(90, 10000, 10000); // (itemSize, minBufferPx, maxBufferPx)
  }
}

@Component({
  selector: 'thumbnail-list',
  template: `
    <div class="mt-1">
      <div class="cdk-scroll-source" style="width: 99%">
        <cdk-virtual-scroll-viewport
          class="cdk-scroll-viewport"
          orientation="horizontal" >
          <ng-container *cdkVirtualFor="let item of _currentImages">
            <thumbnail-item [originalImage]="item"
                            [addClass]="addClass"
                            (selected) = onSelectItem($event)>
            </thumbnail-item>
          </ng-container>
        </cdk-virtual-scroll-viewport>
      </div>
    </div>  `,
  styles: [`
    .cdk-scroll-source {
      writing-mode: vertical-lr;
    }
    .cdk-scroll-source .cdk-scroll-viewport {
      height: 90px;
      width: 100%;
    }
    .cdk-scroll-source .cdk-scroll-viewport .cdk-virtual-scroll-content-wrapper {
      display: flex;
      flex-direction: row;
    }
  `],
  providers: [{provide: VIRTUAL_SCROLL_STRATEGY, useClass: CustomVirtualScrollStrategy}],
  changeDetection: ChangeDetectionStrategy.OnPush

})
export class ThumbnailListComponent implements OnInit, AfterViewInit {
  @Input() set selectedImage (v: any){
    if( v ) {
      v && this.onSelectItem(v.item);
      this.cdr.markForCheck();
    }
  };
  @Input() set currentImages (im:  any) {
    // console.log(' currentImages', im)
    this._currentImages = im;
    this.cdr.markForCheck();
  }
  @Output() selectItem = new EventEmitter<any>();
  @ViewChild(CdkVirtualScrollViewport, { static: true }) viewPort: CdkVirtualScrollViewport | undefined;
  @Select(StatusState.getSelectedImageById)  getSelectedImageById$: Observable<ImageModel>;
  _currentImages: any;
  addClass: {} = {};
  draggedInx = 0;
  idx = 0;

  constructor(
    private cdr: ChangeDetectorRef,
  ) { }

  ngOnInit(): void {
    const initial_value = {
      item: {
        imageId: 1,
        category: 'animal',
        url: '',
        blob: '',
        title: ''
      }
    }
    localStorage.setItem('selectedImageId', JSON.stringify(initial_value));
    /**
     * Triggered from series-list.component ( onSelectSeries),
     *      carousel.service (getPrevImage, getNextImage)
     */
    this.getSelectedImageById$.pipe(
      // takeUntil(this.unsubscribe$)
      take(1)
    ).subscribe( (image: any) => {

      // To synchronize with the current selected item, after when it is activated by clicking item-list
      localStorage.setItem('selectedImageId', JSON.stringify({item: image}));
      setTimeout(() => {
      // console.log(' data1 -', image.imageId)
        this.viewPort.scrollToIndex(image.imageId, 'smooth')
        this.addClass = {
          class:'selected_item',
          imageId: image.imageId
        }
      this.cdr.markForCheck();
      },200);
    })
    ///
  }
  ngAfterViewInit() {
    // Because of not focused with red border even though clicking item just after staring.
    setTimeout(()=> {
      this.addClass = {
        class: 'selected_item',
        index: 1
      }
    })
  }

  onSelectItem(ev:ImageModel) {
    if( !ev) return;

    // console.log( '--- thumbnail-list id', ev )
    localStorage.setItem('selectedImageId', JSON.stringify({item:ev}));
    this.selectItem.emit(ev); // send to home.component
    /**
     * To synchronize with the current selected item, after when it is activated by clicking item-list
     * */
    setTimeout(() => {
      this.viewPort?.scrollToIndex(ev.imageId, 'smooth')
      this.addClass = {
        class: 'selected_item',
        index: ev.imageId
      }
      this.cdr.detectChanges();
    },200);
  }
}

import {Injectable, OnDestroy} from '@angular/core';
import { HttpClient } from '@angular/common/http';
import {Observable, of, Subject} from 'rxjs';
import {map, tap} from 'rxjs/operators';
import {StatusState} from "../../state/status/status.state";
import {SelectSnapshot} from "@ngxs-labs/select-snapshot";
import {downscaleImage} from "../utils/down-scale-image";
import {SetImageUrls, SetIsImageLoaded} from "../../state/status/status.actions";
import {ImageModel} from "../models/data";


@Injectable({
  providedIn: 'root'
})
export class ImageService implements  OnDestroy {
  unsubscribe = new Subject();
  unsubscribe$ = this.unsubscribe.asObservable()
  private _cacheUrls: {
    idx: number,
    category: string,
    url: string
  }[] = [];
  private _cachedImages: {
    idx: number,
    category: string,
    image: ImageModel
  }[] = [];
  private _cachedThumbnailImages: {
    idx: number,
    category: string,
    image: ImageModel
  }[] = [];
  @SelectSnapshot(StatusState.getCurrentCategory) category: string;
  @SelectSnapshot(StatusState.getCategoryList) category_list: string[];
  constructor(private http: HttpClient) { }

  isThisUrlCached(url: string) {
    return this._cacheUrls.find(val => val.url === url);
  }
  setCacheUrl(data:any) { // data: ImageModel
    const cIdx: any = this.category_list.findIndex( val => val === data.category) + 1;
    const nIdx = data.imageId < 10 ? (cIdx * 10 + data.imageId) : (cIdx * 100 + data.imageId);
    const nUrl = { idx: nIdx, category: data.category, url: data.url};
    // [{idx:10, url:'aaa'}, {idx:11, url:'bbb'}]
    this._cacheUrls = [...this._cacheUrls,nUrl];
    // console.log(' nUrl', nUrl, this._cacheUrls, cIdx)
  }

  getCacheUrls() {
    return this._cacheUrls;
  }
  getCacheUrlsByCategory(cat: string) {
    return this._cacheUrls.filter(val => val.category === cat);
  }

  set cachedImages(data: any) { // data: ImageModel
    const cIdx: any = this.category_list.findIndex( val => val === data.category) + 1;
    const nIdx = data.imageId < 10 ? (cIdx * 10 + data.imageId) : (cIdx * 100 + data.imageId);
    const image: ImageModel = data.image;
    this._cachedImages.push({idx:nIdx, category:data.category, image:image});
  }
  // @ts-ignore
  get cachedImages(): any[] {
    return this._cachedImages;
  }
  get cachedThumbnailImages(): any[] {
    return this._cachedThumbnailImages;
  }
  getTotalImageList(url: string) {
    // console.log('--- url', url);
    // console.time('cccc')
    return this.http.get(url).pipe(
      map ( (res:any) => {
        // console.timeEnd('cccc')
        return res['data']
      }),
    )
  }

  getCacheImage(cat: string, idx: number) {
    // console.log('getCacheImage', cat, idx)
    const cIdx: any = this.category_list.findIndex( val => val === cat) + 1;
    const nIdx = idx < 10 ? (cIdx * 10 + idx) : (cIdx * 100 + idx);
    const index = this._cachedImages.findIndex(image => image.idx === nIdx);
    if (index > -1) {
      const res = this._cachedImages.filter(val => val.idx === nIdx);
      return res[0].image.blob;
    }
    return ('')
  }

  checkAndCacheImage(data: ImageModel) {
    const cIdx: any = this.category_list.findIndex( val => val === data.category) + 1;
    const nIdx = data.imageId < 10 ? (cIdx * 10 + data.imageId) : (cIdx * 100 + data.imageId);
    //
    const ret = this._cacheUrls.find( val => val.idx === nIdx)
    if( ret ) return;
    this.setCacheUrl(data);
    this._cachedImages.push({idx: nIdx, category: data.category, image: data});
    /** Save image data as thumbnail */
    let tData = Object.assign({}, data);
    const file = downscaleImage(tData.blob, 'image/jpeg', 100,0.7);
    file.then( val => {
      tData.blob = val;
      this._cachedThumbnailImages.push({idx: nIdx, category: tData.category, image: tData});
    })

  }
  makeCachedImage(data: ImageModel): Observable<any>{
    if(  !data.blob.type || !data.blob.size) {
      // data is not Blob type.
      return of(undefined);
    }
    const image: any = this.readFile(data.blob)
    return image.pipe(
      map((obj: any) =>  {
        data.blob = obj;
        this.checkAndCacheImage(data)
        return data
      }),
      // tap( val => console.log('data1', val)),
    )
  }

  readFile (blob: any): Observable<string>  {
    return new Observable((obs: any) => {
      const reader = new FileReader();

      reader.onerror = err => obs.error(err);
      reader.onabort = err => obs.error(err);
      reader.onload = () => obs.next(reader.result);
      reader.onloadend = () => obs.complete();

      return reader.readAsDataURL(blob);
    });
  }
  ngOnDestroy() {
    this.unsubscribe.next({});
    this.unsubscribe.complete();
  }
}

import {Injectable, OnDestroy} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {concatMap, map, takeUntil, toArray} from "rxjs/operators";
import {from, Observable, Subject} from "rxjs";
import {SelectSnapshot} from "@ngxs-labs/select-snapshot";
import {StatusState} from "../../../state/status/status.state";

@Injectable({
  providedIn: 'root'
})
export class SeriesItemService implements OnDestroy{
  unsubscribe = new Subject();
  unsubscribe$ = this.unsubscribe.asObservable();
  @SelectSnapshot(StatusState.getCategoryList) category_list: string[] | undefined;
  constructor(private http: HttpClient) { }

  getSeriesObject(): Observable<any[]> {
     const url_base = 'assets/json/';
     // @ts-ignore
    return from( this.category_list).pipe(
       takeUntil(this.unsubscribe$),
       map( cat => `${url_base}${cat}.json`),
       concatMap( url => this.http.get(url)),
       map( (dat: any, index) => {
         // @ts-ignore
         return {
           url: dat.data[0].url,
           seriesId: index + 1,
           blob: '',
           category: this.category_list[index]
         }
       }),
       toArray()
     )
  }
  ngOnDestroy() {
    this.unsubscribe.next({});
    this.unsubscribe.complete();
  }
}

import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class SpinnerHandlerService {

  public numberOfRequests: number = 0;
  public showSpinner: BehaviorSubject<boolean> = new BehaviorSubject<boolean>(false);

  handleRequest = (state: string = 'minus'): void => {
    this.numberOfRequests = (state === 'plus') ? this.numberOfRequests + 1 : this.numberOfRequests - 1;
    this.showSpinner.next(this.numberOfRequests > 0);
  };

}

import { Injectable } from '@angular/core';
import {Observable, Subject} from "rxjs";
import {Store} from "@ngxs/store";
import {SelectSnapshot} from "@ngxs-labs/select-snapshot";
import {StatusState} from "../../state/status/status.state";

@Injectable({
  providedIn: 'root'
})
export class SplitService {
  bWorker: any = undefined;

  elements: any[] = ['element1', 'element2', 'element3', 'element4']
  selectedElement: any = this.elements[0];
  /** Each category has image id, which is changed by clicking thumbnail item and by
   * clicking previous arrow key  and next arrow key */
  currentImageIndex: {[key:string]: number} = {};
  imageCount: {[key:string]:number} = {};
  progress: {[key:string]:number} = {};

  isFinishedRendering: {[key:string]: Subject<any>} = {};
  isFinishedRendering$: {[key:string]: Observable<any>} = {};
  isStartedRendering: {[key:string]: Subject<any>} = {};
  isStartedRendering$: {[key:string]: Observable<any>} = {};
  //
  @SelectSnapshot(StatusState.getCategoryList) category_list:string[];

  constructor(private store: Store) { }
  resetSplitWindowProcessing() {
    this.category_list.map( val => this.currentImageIndex[val] = 0 )
    this.elements.map( val => {
      this.isFinishedRendering[val] = new Subject();
      this.isFinishedRendering$[val] = this.isFinishedRendering[val].asObservable();
      this.isStartedRendering[val] = new Subject();
      this.isStartedRendering$[val] = this.isStartedRendering[val].asObservable();
    });
  }
}

import { State, Action, Selector, StateContext } from '@ngxs/store';
import {
  SelectedGridTemplate,
  SetActiveSplit, SetCategoryList,
  SetCurrentCategory, SetCurrentSplitOperation, SetFocusedSplit,
  SetImageUrls,
  SetIsImageLoaded,
  SetIsSeriesLoaded,
  SetSelectedImageById,
  SetSelectedImageByUrl,
  SetSelectedSeriesById,
  SetSelectedSplitWindowId, SetSeriesUrls, SetSplitAction,
  SetSplitMode,
  SetSplitCategory, SetWebworkerWorkingStatus,
  StatusAction
} from './status.actions';
import {Injectable} from "@angular/core";
import {ImageModel} from "../../app/models/data";
export interface ISelectedGridTemplate {
  templateName: string,
  button: 'left' | 'right'
}
export interface StatusStateModel {
  selectedGridTemplate: ISelectedGridTemplate;
  items: string[];
  isImageLoaded: {} ; // from 0
  isSeriesLoaded: boolean;
  imageUrls: string[]; //
  seriesUrls: string[]; //
  currentCategory: string;
  category_list: string[];
  focusedSplit: number;
  selectedImageId: ImageModel;
  selectedImageUrl: string;
  splitMode: number;
  splitCategory: string[];
  splitAction: boolean;
  selectedSeriesById: number;
  selectedSplitWindowId: string;
  webworkerWorkingStatus: boolean;
  currentSplitOperation: {},
  activeSplit: number
}

@State<StatusStateModel>({
  name: 'status',
  defaults: {
    selectedGridTemplate: {templateName: '', button: 'left'},
    items: [],
    isImageLoaded: { idx: 0},
    isSeriesLoaded: false,
    imageUrls: [],
    seriesUrls: [],
    currentCategory: '',
    //category_list:['animal', 'house', 'baby','forest', 'happiness', 'love','sea','banana' ],
    category_list:['animal', 'house', 'baby', 'forest', 'happiness', 'love', 'sea','banana', 'mountain'],
    focusedSplit: 0, // 0: split1, 1: split2, 2: split3, 3: split4
    selectedImageId: {
      imageId: 0,
      category: 'animal',
      url: '',
      blob: '',
      title: ''
    },
    selectedImageUrl: '',
    splitMode: 1, // 1: active --> split1, 2: active --> split1, split2
    splitCategory: ['animal', 'house', 'baby', 'forest'],
    splitAction: false,
    selectedSeriesById: 0,
    selectedSplitWindowId: 'element1',
    webworkerWorkingStatus: false,
    currentSplitOperation: {
      element: ''
    },
    activeSplit: 0
  }
})
@Injectable()
export class StatusState {

  @Selector()
  public static getSelectedGridTemplate(state: StatusStateModel) {
    return state.selectedGridTemplate;
  }
  @Selector()
  public static getState(state: StatusStateModel) {
    return state;
  }
  @Selector()
  public static getIsImageLoaded(state: StatusStateModel) {
    return state.isImageLoaded;
  }
  @Selector()
  public static getIsSeriesLoaded(state: StatusStateModel) {
    return state.isSeriesLoaded;
  }
  @Selector()
  public static getImageUrls(state: StatusStateModel) {
    return state.imageUrls;
  }
  @Selector()
  public static getSeriesUrls(state: StatusStateModel) {
    return state.seriesUrls;
  }
  @Selector()
  public static getCurrentCategory(state: StatusStateModel) {
    return state.currentCategory;
  }
  @Selector()
  public static getCategoryList(state: StatusStateModel) {
    return state.category_list;
  }
  @Selector()
  public static getFocusedSplit(state: StatusStateModel) {
    return state.focusedSplit;
  }
  @Selector()
  public static getSelectedImageById(state: StatusStateModel) {
    return state.selectedImageId;
  }
  @Selector()
  public static getSelectedImageByUrl(state: StatusStateModel) {
    return state.selectedImageUrl;
  }
  @Selector()
  public static getSplitMode(state: StatusStateModel) {
    return state.splitMode;
  }
  @Selector()
  public static getSplitCategories(state: StatusStateModel) {
    return state.splitCategory;
  }
  @Selector()
  public static getSplitAction(state: StatusStateModel) {
    return state.splitAction;
  }
  @Selector()
  public static getSelectedSeriesById(state: StatusStateModel) {
    return state.selectedSeriesById;
  }
  @Selector()
  public static getSelectedSplitWindowId(state: StatusStateModel) {
    return state.selectedSplitWindowId;
  }
  @Selector()
  public static getWebworkerWorkingStatus(state: StatusStateModel) {
    return state.webworkerWorkingStatus;
  }
  @Selector()
  public static getCurrentSplitOperation(state: StatusStateModel) {
    return state.currentSplitOperation;
  }
  @Selector()
  public static getActiveSplit(state: StatusStateModel) {
    return state.activeSplit;
  }

  @Action(SelectedGridTemplate)
  public selectedGridTemplate({patchState, getState}: StateContext<StatusStateModel>, { payload }: SelectedGridTemplate) {
    const obj = getState().selectedGridTemplate;
    // console.log(' payload', payload)
    patchState({selectedGridTemplate: {...obj, ...payload}});
  }
  @Action(StatusAction)
  public add(ctx: StateContext<StatusStateModel>, { payload }: StatusAction) {
    const stateModel = ctx.getState();
    stateModel.items = [...stateModel.items, payload];
    ctx.setState(stateModel);
  }
  @Action(SetIsImageLoaded)
  public setIsImageLoaded({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetIsImageLoaded) {
    const obj = getState().isImageLoaded;
    patchState({isImageLoaded: {...obj, ...payload}})
  }
  @Action(SetIsSeriesLoaded)
  public setIsSeriesLoaded({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetIsSeriesLoaded) {
    patchState({isSeriesLoaded: payload})
  }
  @Action(SetImageUrls)
  public setImageUrls({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetImageUrls) {
    let urls = getState().imageUrls;
    patchState({imageUrls: [...urls, ...payload]});
  }
  @Action(SetSeriesUrls)
  public setSeriesUrls({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetSeriesUrls) {
    let urls = getState().seriesUrls;
    patchState({seriesUrls: [...urls, ...payload]});
  }
  @Action(SetCurrentCategory)
  public setCurrentCategory({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetCurrentCategory) {
    patchState({currentCategory: payload})
  }
  @Action(SetCategoryList)
  public setCategoryList({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetCategoryList) {
    patchState({category_list: payload})
  }
  @Action(SetFocusedSplit)
  public setFocusedSplit({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetFocusedSplit) {
    patchState({focusedSplit: payload})
  }
  @Action(SetSelectedImageById)
  public setSelectedImageById({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetSelectedImageById) {
    const imageId = getState().selectedImageId;
    patchState({selectedImageId: {...imageId, ...payload}})
  }
  @Action(SetSelectedImageByUrl)
  public setSelectedImageByUrl({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetSelectedImageByUrl) {
    patchState({selectedImageUrl: payload})
  }
  @Action(SetSplitMode)
  public setSplitMode({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetSplitMode) {
    patchState({splitMode: payload})
  }
  @Action(SetSplitCategory)
  public setSplitCategory({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetSplitCategory) {
    const state = getState().splitCategory;
    const idx = payload.idx;
    const category = payload.category
    state[idx] = category
    patchState({splitCategory: [...state]})
  }
  @Action(SetSplitAction)
  public setSplitAction({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetSplitAction) {
    patchState({splitAction: payload})
  }
  @Action(SetSelectedSeriesById)
  public setSelectedSeriesById({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetSelectedSeriesById) {
    patchState({selectedSeriesById: payload})
  }
  @Action(SetSelectedSplitWindowId)
  public setSelectedSplitWindowId({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetSelectedSplitWindowId) {
    patchState({selectedSplitWindowId: payload})
  }
  @Action(SetWebworkerWorkingStatus)
  public setWebworkerWorkingStatus({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetWebworkerWorkingStatus) {
    patchState({webworkerWorkingStatus: payload})
  }
  @Action(SetCurrentSplitOperation)
  public setCurrentSplitOperation({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetCurrentSplitOperation) {
    const obj = getState().currentSplitOperation;
    patchState({currentSplitOperation: { ...obj, ...payload }})
  }
  @Action(SetActiveSplit)
  public setActiveSplit({patchState,getState}: StateContext<StatusStateModel>, { payload }: SetActiveSplit) {
    patchState({activeSplit: payload})
  }

}

